.file-header {
    background-color: #ffffff;
    border: 1px solid #ddd;
    border-radius: 2px !important;
    margin-top: 10px;
}
.file {
    border: unset;
    border-radius: 2px;
    width: calc(100% - 50px);
    margin: 0 auto;
}
