    color: #fff;
}
.commit-tease .message {
    color: #b7b7b7;
}
.commit-tease-contributors {
    background-color: #fff;
    border-top: unset;
    border-bottom-right-radius: 0px !important;
    border-bottom-left-radius: 2px;
    border: unset;
}
