}
.file .blob-wrapper tbody tr:hover .blob-num,
.blob-num:hover {
    color: var(--primary-color);
}
.file .blob-wrapper tbody tr {
    transition: .1s linear background-color;
}
.file .blob-wrapper tbody tr:hover {
    background-color: rgba(0, 0, 0, 0.1);
}
.menu-heading {
