.page-content nav.menu:nth-child(2) {
    margin-top: 40px !important;
}
.Subhead-heading,
.Box label,
.form-group label {
    color: #24292e;
}
.mt-4:nth-child(1) div:not(.js-yearly-contributions)  h2.f4.mb-2.text-normal {
