@namespace url(http://www.w3.org/1999/xhtml);
/*
 * Basic layout
 */

.container {
  width: 100% !important;
  padding-right: 30px !important;
  padding-left: 30px !important;
}

/* Responsive containers on some pages */
.container-lg,
.container-xl {
  max-width: 100% !important;
  padding-right: 30px !important;
  padding-left: 30px !important;
}

/* Match the header to container padding */
.Header {
  padding-right: 30px !important;
  padding-left: 30px !important;
}


/*
 * Profile pages
 */

.pinned-repo-item {
  width: calc(50% - 10px) !important;
}
.org-pinned-repos-list .pinned-repo-item {
  width: calc(33.3% - 10px) !important;
}

.u-photo,
.user-status-container {
  max-width: 250px;
}
.u-photo .avatar {
  width: 100% !important;
  height: auto !important;
}


/*
 * Repo-specific stuff
 */

.repository-content {
  width: 100% !important;
}

/* Undo container changes for the Projects tab on repos which have unnecessary nested container-lg's */
.repository-content > .container-lg {
  padding-right: 0 !important;
  padding-left: 0 !important;
}

/* Undo for profile timeline */
.contribution-activity-listing .discussion-timeline {
  width: 100% !important;
}

/* Fix #18 - props: @auscompgeek */
.file-header::after {
  clear: left !important;
}

/* Network graph */
#network {
  max-width: 730px;
}


/*
 * Issues & Dashboard
 */

#dashboard,
.new-issue-form {
  position: relative !important;
}

.new-issue-form .discussion-sidebar {
  position: absolute !important;
  top: 0 !important;
  right: 0 !important;
}

button.discussion-sidebar-toggle {
  width: 100% !important;
}

.timeline-new-comment {
  max-width: none !important;
}

/* Commits: extended message under "..." */
.commit-desc pre {
  max-width: none;
}

/* Alert messages */
#js-flash-container .flash-messages {
  width: 100% !important;
  padding-left: 30px !important;
  padding-right: 30px !important;
}

.tab-size /* override GitHub’s guesses of correct tab size */ {
        -moz-tab-size: 4 !important;
        tab-size: 4 !important;
    }
    
    .type-ruby .tab-size, /* Ruby in files and Gists */
    .file[data-file-type=".rb"] .tab-size /* Ruby in diffs */ {
        -moz-tab-size: 2 !important;
        tab-size: 2 !important;
    }

header {
	-webkit-animation: rainbow-bg 20s infinite ease-in-out;
          animation: rainbow-bg 20s infinite ease-in-out;
}

.notification-indicator .mail-status {
  -webkit-animation: rainbow-border 20s infinite ease-in-out;
          animation: rainbow-border 20s infinite ease-in-out;
}

header .header-search-scope {
  -webkit-animation: rainbow-border 20s infinite ease-in-out;
          animation: rainbow-border 20s infinite ease-in-out;
}


/* #E91E63 #673ab7 #3f51b5 #00bcd4 #009688 #4caf50 #8bc34a #ffc107 #ff9800 #ff5722 */
@-webkit-keyframes rainbow-bg {
  0% {
    background: #E91E63;
  }
  10% {
    background: #673ab7;
  }
  20% {
    background: #3f51b5;
  }
  30% {
    background: #00bcd4;
  }
  40% {
    background: #009688;
  }
  50% {
    background: #4caf50;
  }
  60% {
    background: #8bc34a;
  }
  70% {
    background: #ffc107;
  }
  80% {
    background: #ff9800;
  }
  90% {
    background: #ff5722;
  }
  100% {
    background: #E91E63;
  }
}

@keyframes rainbow-bg {
  0% {
    background: #E91E63;
  }
  10% {
    background: #673ab7;
  }
  20% {
    background: #3f51b5;
  }
  30% {
    background: #00bcd4;
  }
  40% {
    background: #009688;
  }
  50% {
    background: #4caf50;
  }
  60% {
    background: #8bc34a;
  }
  70% {
