}
.bilibili-player-video-btn-send.bpui-button:hover:after {
    font-family: 'Material Icons';
    content: "\e163";
    font-size: 20px;
    position: absolute;
    right: 80px;
    top: 5px;
    color: #FFF;
    line-height: 28px;
    transition: all 0.3s;
    animation: in_out_2 0.6s;
    animation-fill-mode: forwards;
}
