    animation: none !important;
}
.bilibili-player-video-btn-send.bpui-button.button.bpui-state-disabled:hover:after {
    animation: none !important;
}
@keyframes in_out_2 {
    0% {
        right: 80px;
        opacity: 0;
    }
    40% {
        right: 36px;
        opacity: 1;
    }
    60% {
        right: 18px;
        opacity: 1;
    }
    100% {
        right: -26px;
        opacity: 0;
    }
}

