    background:none;
}
/*右侧排行榜*/
.page-content .right-side .rank-module{
    transition: all 0.3s;
    background:var(--boxback,rgba(255, 255, 255, 0.4));
    box-shadow: 0 0 10px 5px rgba(0, 0, 0, .05);
    padding:10px 10px 10px 10px;/*边框10*/
    border-radius: 5px;
}
.page-content .right-side .rank-module:hover{
    background:var(--boxbacks,rgba(255, 255, 255, 0.85));
    box-shadow: 0 0 3px 1px rgba(0, 0, 0, .2);
}
.page-content .right-side .rank-module .rank-list .item{
/*     transition: all 0.2s; */
    background:none;
    position:relative;
/*     border-radius: 4px;
    padding:0px; */
}
.page-content .right-side .rank-module .rank-list .item:hover{
/*     background:var(--boxbacks,rgba(255, 255, 255, 0.85));
    box-shadow: 0 0 3px 1px rgba(0, 0, 0, .2);
    padding:0px 10px 0px 10px; */
    margin-left:-3px;
}
.right-side .rank-module .rank-list .item a:after{
    content: "";display: block;position: absolute;height:calc(100% - 0.1rem);width: 0;top: 0;left:100%;
    transition: all .45s var(--v1);opacity: 1;/*clear:both;*/
    border-bottom:0.1rem solid;/*直接画线会导致块变大qwq，所以提前画好，透明就看不见了*/
}
.right-side .rank-module .rank-list .item:hover a:after{
    width:100%;opacity: 1;left: 0;
    transition:all .45s var(--v1), left 0s;/*时间曲线来源于https://easings.net/#easeOutCubic*/
    /*animation:fromleft .75s ease 0s 1;视觉效果是从左出现底部线*/
}
/*右侧最新投稿UP*/
.page-content .right-side .up-list .up-item .info-holder{
