    box-shadow: 0 0 5px 2px rgba(0, 0, 0, 0.2);
}
.up-info-holder .fixed-box .follow-btn-holder{
    margin-top:-75px;/*B站前端的锅，关注按钮不放在UP主信息的块里*/
